    position: relative;
    background: $darkish-blue;
    color: #fff;

    &__header{
        display: flex;
        align-items: center;
        gap: 1rem;

        .instructor{
            position: relative;
            background: #28316013;
            box-shadow: 0 8px 32px 0 $box-shadow-blue1;

            &__curve{
                position: absolute;
                top: -10px;
                left: 20px;
                width: 30%;
                height: 120%;
                // background: red;
                border-left: 2px solid $red-Color;
                border-radius: 50%;
                transform: rotate(-20deg);
                box-shadow: 0 8px 32px 0 $box-shadow-blue2;
            }

            &__infos{
                display: flex;
                align-items: center;
                gap: 15px;
                font-size: 17px;
                font-family: sans-serif;
            }

            &__img{
                width: 110px;
                height: 110px;
                border-radius: 50%;
                border-right: 2px solid $red-Color;

                @media(min-width:400px){
                    &{
                        width: 150px;
                        height: 150px;
                    }
                }
            }

            &__col{
                display: flex;
                flex-direction: column;
                gap: 5px;
            }

            &__info{
                display: flex;
                align-items: center;
                gap: 10px;

                & img{
                    width: 20px;
                    height: 20px;
                }
            }
        }
    }
}

.swiper__wrapper{
    padding: 2rem 0;
}

.swiper{
    // min-height: 500px;
    user-select: none;
}

.review{
    position: relative;
    display: flex;
    font-size: 16px;
    padding: 5px;

    &__card{
        position: relative;
        width: 100%;
        padding: 2rem 0;
        display: flex;

        @media (max-width:650px){
            &{
                font-size: 18px;
                font-family: sans-serif;
            }
        }
    }

    & svg{
        width: 30px;
        fill: $red-Color;
    }
    &__topborder{
        width: 100%;
        height: 1px;
        position: absolute;
        top: 0;
        left: 0;
        background:#fff;
    }

    &__text{
        user-select: none;
        span{
            font-family: 'Haghisty_DEMO', sans-serif;
            font-size: 30px;
            font-weight: 600;
            color: $red-Color;
