    transform: scale(0.8) !important;
    transition: all 0.2s;
}

.swiper-slide-active{
    filter: blur(0) !important;
    transform: scale(1) !important;
}

.swiper__tools{
    position: relative;
    display: flex;
    flex-direction: column-reverse;
    gap: 2rem;
