// @import './base.scss';
.landing{
    position: relative;
    width: 100%;
    height: 100vh;
    background: #111;
    color: #fff;
    overflow: hidden;
    // background-image: url('../images/wallpapers/10.jpg');
    background: transparent;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    .header{
        position: absolute;
        top: -100%;
        left: 50%;
        transform: translateX(-50%);
        // border: 2px solid red;
        z-index: 2;

        &__wrap{
            padding: 10px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            width: 60vw;
        }

        &__left{
            display: flex;
            align-items: center;
            gap: 2rem;

            & li{
                font-size: 18px;
                font-weight: 700;
            }
        }

        @media (max-width:1200px){
            &__wrap{
                width: 100vw;
            }
        }
        @media (max-width:650px){
            &__sm{
                display: none;
            }
        }
        @media (max-width:370px){
            &{
                left: 36%;
            }

            &__wrap{
                gap: 20%;
            }

            .coolLink[data-text="Home"]{
                display: none;
            }
        }
    }

    .socials{
       position: absolute;
       left: 50%;
       transform: translateX(-50%);
       bottom: -100%;
       display: flex;
       align-items: center;
       gap: 1rem;
       z-index: 2;
    //    border: 2px solid red;
        // @media (max-width:1134px) {
        //     transform: translateX(70%);
        // }
        // @media (max-width:421px) {
        //     transform: translateX(-50%);
        //     transform: translateY(70%);
        // }
    }

    .scrollDown{
        position: absolute;
        bottom: -100%;
        left: 50%;
        transform: translateX(-50%);
        cursor: pointer;
        // border: 2px solid red;
        z-index: 1;
        @media (max-width:421px) {
                transform: translateX(50%);
                left: 45%;
                transform: translateY(40%);
            }
       

        &__wheel{
            height: 42px;
            width: 24px;
            border-radius: 10px;
            border: 2px solid #fff;
            cursor: pointer;

            &--inner{
                position: absolute;
                left: 10px;
                margin-top: 5px;
                height: 4px;
                width: 4px;
                border-radius: 50%;
                background: #fff;
                animation: movingwheel 0.5s linear infinite;

                @keyframes movingwheel{
                    0%{
                        top: 1px;
                    }
                    25%{
                        top: 2px;
                    }
                    50%{
                        top: 3px;
                    }
                    75%{
                        top: 2px;
                    }
                    100%{
                        top: 1px;
                    }
                }
                
            }
        }

        .scrollDown__arrows{
            display: flex;
            align-items: center;
            flex-direction: column;

            .scrollDown__arrow{
                width: 15px;
                height: 15px;
                border-right:2px solid #fff;
                border-bottom:2px solid #fff;
                transform: rotate(45deg);
                animation: arrowsAnim 1s infinite;
                animation-direction: alternate;
                &:nth-child(1){
                   animation-delay: 0.1s;
                }
                &:nth-child(2){
                   animation-delay: 0.2s;
                }
                &:nth-child(3){
                   animation-delay: 0.3s;
                }

                @keyframes arrowsAnim {
                    0%{
                        opacity: 0;
                    }
                    50%{
                        opacity: 0.5;
                    }
                    100%{
                       
                        opacity: 1;
                    }
                }
            }
        }
    }

    .intro{
        position: absolute;
        top: 20%;
        left: 1rem;
        width: 90%;
        height: 70%;
        z-index: 2;
        color: #fff;
        // font-family: 'Nulshock', sans-serif;
        font-family: 'Noir-et-Blanc', sans-serif;
        

        & h4{
            font-size: 33px;
        }

        & h1{
            font-size: 35px;
            width: 93%;
            padding-top: 1rem;
        }

        @media (min-width:600px){
            & h1{
                font-size: 55px;
            }
            & h4{
                font-size: 40px;
            }

        }
        @media (min-width:1200px){
            & {
                left: 5%;
            }
            & h1{
                font-size: 70px;
            }
            & h4{
                font-size: 50px;
            }

        }
        // @media (min-width:343px) {
        //     width: 95%;

        //     & h1 {
        //         font-size: 30px;
        //     }

        //     & h4 {
        //         font-size: 30px;
        //     }

        // }
        // @media (max-width:355px) {
        //     &__sm {
        //         display: none;
        //     }
        // }
    }

}

.landing canvas{
    position: absolute;
    // z-index: 1;
}
